import React from "react";

export default function usePlayer([videoFile, setVideoFile], [selectedPlaylist, setSelectedPlaylist]){

    function PlayPause(playing){
        const newVideoFile = {...videoFile}
        newVideoFile.playing = playing
        setVideoFile(newVideoFile)
    }
    
    function back(){
        let musicFind = {}
        if(!videoFile.back){
            console.log(selectedPlaylist)
            musicFind = selectedPlaylist.music[selectedPlaylist.music.length-1]
        }else{
            let arrayMusicsFind = selectedPlaylist.music.filter((music)=>music.value._id == videoFile.back._id)
            musicFind = arrayMusicsFind[0]
        }
        const newVideoFile = {...videoFile}
        newVideoFile.value = musicFind.value
        musicFind.playing = videoFile.playing
        setVideoFile(musicFind)
    }

    function next(){

    }

    function handleSeekChange(){

    }

    return { 
        PlayPause,
        back,
        next,
        handleSeekChange,
    }
}
